import { Box } from "@mui/system";
import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { MICROSITE_CONNECT_ACCOUNT_SUCCESS, MICROSITE_CONNECT_MORE_ACCOUNTS, trackEvent } from "../../analytics";
import { Background, FullScreenLoader, NavBar } from "../../Components";
import { ROUTE_LANDING_PAGE } from "../../Routes/RouteMapping";
import { getWorkplatformDetails } from "../../utils/api";
import { CONNECTED_STORAGE_WORK_PLATFORM_ID, STORAGE_AUTH_DATA, STORAGE_AUTH_TENANT_APP_NAME } from "../../utils/constants";
import "./SuccessPage.scss";

function SuccessPage() {
  const navigate = useNavigate();
  const [appName, setAppName] = useState();
  const onlyOnce = useRef(true);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    (async () => {
      if (sessionStorage.getItem(STORAGE_AUTH_DATA)) {
        const auth = JSON.parse(sessionStorage.getItem(STORAGE_AUTH_DATA));
        if (onlyOnce.current) {
          onlyOnce.current = false;
          const workPlatformId = sessionStorage.getItem(CONNECTED_STORAGE_WORK_PLATFORM_ID);
          const response = await getWorkplatformDetails(workPlatformId);
          trackEvent(MICROSITE_CONNECT_ACCOUNT_SUCCESS, { creator_user_id: auth?.user_id, work_platform_name: response?.name });
        }
        setAppName(auth[STORAGE_AUTH_TENANT_APP_NAME]);
      }
    })();
  }, []);

  if (!appName) return <FullScreenLoader showLoader={isLoading} />;

  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <Box className="success-page-body">
          <label className="success-title-msg">Thank you for connecting your accounts!</label>
          <label className="success-description-msg">
            That was easy! You can now choose to{" "}
            <a
              href="#"
              onClick={() => {
                trackEvent(MICROSITE_CONNECT_MORE_ACCOUNTS);
                navigate(ROUTE_LANDING_PAGE);
              }}
            >
              connect more accounts
            </a>{" "}
            if you'd like. If you have questions or concerns, please contact {appName}.
          </label>
        </Box>
      </Background>
    </>
  );
}

export default SuccessPage;
