import { useEffect, useRef, useState } from "react";
import { Box } from "@mui/system";
import React from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import Icons from "../../Components/Icons/icons";
import { Background, ButtonWithGlyphIcon, FullScreenLoader, NavBar, PrimaryButton } from "../../Components/index";
import InputField from "../../Components/InputField/InputField";
import "./LandingPage.scss"
import TagsFilter from "../../Components/TagsFilter/TagsFilter";
import { sendData } from "../../utils/api";
function LandingPage() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [urlSearchInput, setUrlSearchInput] = useState("");
  const [selectedTags, setSelectedTags] = useState([]);


  const  handleShowResults = () =>{
    sendData(urlSearchInput)
  }

  if (isLoading) {
    return <FullScreenLoader showLoader={isLoading} />;
  } 
  console.log(urlSearchInput,selectedTags)

  return (
    <>
      <Background>
        <Box className="landing-page-body">
          <InputField
            inputLabel="Enter Image Url"
            placeholder="start typing"
            value={urlSearchInput}
            onChange={(e) => {
              setUrlSearchInput(e.target.value);
            }}
            autoFocus
          />
          <Box sx={{marginTop:"30px"}}>
            <label htmlFor="input-field" className="input-label" >
              Enter Keyword
            </label>
            <TagsFilter type="multi_select_text" selectedTags={selectedTags} setSelectedTags={setSelectedTags} />
          </Box>
          <Box className="showResultButton">
          <PrimaryButton label="Show Results" onClick={handleShowResults}/>
          </Box>
        </Box>
      </Background>
    </>
  );
}

export default LandingPage;
