import { STORAGE_AUTH_DATA, STORAGE_AUTH_SDK_TOKEN } from "./constants";

const BASE_URL = process.env.REACT_APP_DASHBOARD_BASE_URL;
const SDK_CONFIG__API = "v1/identity/creator-invites/{invite_code}/sdk-config";
const SDK_WORKPLTFORM_API = "v1/internal/work-platforms";

export const getSDKConfig = async (invite_code, env) => {
  try {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      environment: env,
    });

    var requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    const response = await fetch(
      `${BASE_URL}/${SDK_CONFIG__API.replace("{invite_code}", invite_code)}`,
      requestOptions
    );
    const data = await response.json();
    return data;
  } catch (e) {
    console.error(e);
    return null;
  }
};

export const getWorkplatformDetails = async (workPlatformId) => {
  try {
    const auth = JSON.parse(sessionStorage.getItem(STORAGE_AUTH_DATA));
    const sdkToken = auth[STORAGE_AUTH_SDK_TOKEN];
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    myHeaders.append("Authorization", `Bearer ${sdkToken}`);

    const response = await fetch(
      `${process.env.REACT_APP_API_ENDPOINT}/${SDK_WORKPLTFORM_API}/${workPlatformId}`,
      {
        headers: myHeaders,
      }
    );
    const data = await response.json();
    return data;
  } catch (e) {
    console.error(e);
    return null;
  }
};
