import React, { useEffect, useRef } from "react";
import { MICROSITE_INSTRUCTIONS_SCREEN_VIEW, trackEvent } from "../../analytics";
import { Background, NavBar } from "../../Components";
import InstagramGenericMessage from "../../Components/GenericMessage/InstagramGenericMessage";
import { STORAGE_WORK_PLATFORM_NAME } from "../../utils/constants";

function InstaEducation() {
  const onlyOnce = useRef(true);

  useEffect(() => {
    const workPlatformName = sessionStorage.getItem(STORAGE_WORK_PLATFORM_NAME);
    if (workPlatformName) {
      if (onlyOnce.current) {
        trackEvent(MICROSITE_INSTRUCTIONS_SCREEN_VIEW, { work_platform_name: workPlatformName });
        onlyOnce.current = false;
      }
    }
  }, []);

  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <InstagramGenericMessage />
      </Background>
    </>
  );
}

export default InstaEducation;
