import { PRODUCTION_ENVIRONMENT, STAGING_ENVIRONMENT, STORAGE_AUTH_DATA, STORAGE_AUTH_SDK_TOKEN, STORAGE_CREATOR_ENVIRONMENT } from "./constants";

// const BASE_URL = process.env.REACT_APP_DASHBOARD_BASE_URL;
const BASE_URL = "https://dashboard-api.dev.getphyllo.com";
const SDK_CONFIG__API = "v1/identity/creator-invites/{invite_code}/sdk-config";
const SDK_WORKPLTFORM_API = "v1/internal/work-platforms";

const url = "http://ec2-34-206-221-117.compute-1.amazonaws.com/v1/ai/caption/generate"; 

export const sendData = async (urlString, urlSearchInput, description, recommendationNumber, wordsLimit, language, platform ) => {
  try {
    const response = await fetch(url, {
      method: "POST",
      body: JSON.stringify({
        "image_url": urlSearchInput,
        "text_caption": description,
        "recommendation_count": recommendationNumber,
        "words_limit": wordsLimit,
        "platform": platform,
        "lang": language
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });

    // Handle the response data if needed
    const data = await response.json();
    console.log(data); // Log or process the response data
  } catch (error) {
    console.error("Error:", error.message);
  }
};
