import { Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useNavigate } from "react-router-dom";
import Icons from "../../Components/Icons/icons";
import {
  Background,
  ButtonWithGlyphIcon,
  NavBar,
} from "../../Components/index";
import {
  ROUTE_GENERIC_PAGE,
  ROUTE_INSTA_EDUCATION_PAGE,
} from "../../Routes/RouteMapping";
import {
  INSTAGRAM_WORK_PLATFORM_ID,
  STORAGE_WORK_PLATFORM_ID,
  TIKTOK_WORK_PLATFORM_ID,
  YOUTUBE_WORK_PLATFORM_ID,
} from "../../utils/constants";
import "./LandingPage.scss";
function LandingPage() {
  const navigate = useNavigate();

  //set workplatform id and navigate
  const navigateToEducationPage = (id) => {
    sessionStorage.setItem(STORAGE_WORK_PLATFORM_ID, id);
    if (id === INSTAGRAM_WORK_PLATFORM_ID) {
      navigate(ROUTE_INSTA_EDUCATION_PAGE);
      return;
    } else {
      navigate(ROUTE_GENERIC_PAGE);
      return;
    }
  };
  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <NavBar />
        <Box className="landing-page-body">
          <label className="creator-name">Hey Peter!</label>
          <label className="description">
            S.H.I.E.L.D has asked you to connect your social media accounts
            where you create content and have an audience following.
          </label>
          <Box className="workflow-btns-container">
            <ButtonWithGlyphIcon
              icon={<Icons.tiktokIcon />}
              text={"Connect TikTok"}
              click={() => navigateToEducationPage(TIKTOK_WORK_PLATFORM_ID)}
            />
            <ButtonWithGlyphIcon
              icon={<Icons.instagramIcon />}
              text={"Connect Instagram"}
              click={() => navigateToEducationPage(INSTAGRAM_WORK_PLATFORM_ID)}
            />
            <ButtonWithGlyphIcon
              icon={<Icons.youtubeIcon />}
              text={"Connect YouTube"}
              click={() => navigateToEducationPage(YOUTUBE_WORK_PLATFORM_ID)}
            />
            <ButtonWithGlyphIcon
              icon={<Icons.otherPlatformsIcons />}
              text={"Connect other platforms"}
              click={navigateToEducationPage}
            />
          </Box>
        </Box>
      </Background>
    </>
  );
}

export default LandingPage;
