import React from "react";
import { InvertedPrimaryButton, PrimaryButton } from "../index";
import "./GenericMessage.scss"
function GenericMessage() {
  return (
    <div className="message-container">
        <h2 className="message-title">Before you proceed, please remember</h2>
      {/* <div>
      <ol style={{marginLeft:"-11px"}} value="1">
        <li className="message-point" >You need to log into your social account using the correct credentials. </li>
        <li className="message-point" >When you log in, we'll need your permission to access your account data (profile, audience, and content). Don't worry - we won't post, modify, or delete anything. </li>
        <li className="message-point" >The account is connected securely through Phyllo. </li>
        <li className="message-point" >You can disconnect your account whenever you choose to.</li>
      </ol>
      </div> */}

      <div>
        <p className="message-point" >Instagram has some mandatory login requirements. This allows us to get your profile, audience, and campaign data.</p>
        <ol value="1">
          <li className="message-point">Your Instagram account needs to be a Creator or Business account.
            <p>See how to convert your account to a Creator account.</p>
            <video width="100%" height="100%"></video>
            <a href="https://help.getphyllo.com/?lang=en">Need more help? Check the help guide here.</a>
          </li>
          <li className="message-point">Your Instagram account needs to be linked to a Facebook Page.
            <p>See how to link your Instagram account to a Facebook page.</p>
            <video width="100%" height="100%"></video>
            <a href="https://help.getphyllo.com/?lang=en">Need more help? Check the help guide here.</a>
          </li>
          <li>See how to link your Instagram account to a Facebook page.</li>
          <li>While logging in, please select the correct combination of Instagram account and Facebook page.</li>
        </ol>
      </div>


      <div className="button-group" >
        <PrimaryButton
          // onClick={}
          htmlType="submit"
          label="Proceed"
          style={{ marginRight: "24px" }}
        // loading={loading}
        />
        <InvertedPrimaryButton
          // onClick={}
          htmlType="submit"
          label="Back"
        // loading={loading}
        />
      </div>
    </div>
  );
}

export default GenericMessage;
