import { Box } from "@mui/system";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Background, NavBar } from "../../Components";
import { ROUTE_LANDING_PAGE } from "../../Routes/RouteMapping";
import {
  STORAGE_AUTH_DATA,
  STORAGE_AUTH_TENANT_APP_NAME,
} from "../../utils/constants";
import LandingPage from "../LandingPage/LandingPage";
import "./SuccessPage.scss";

function SuccessPage() {
  const navigate = useNavigate();
  const [appName, setAppName] = useState();

  useEffect(() => {
    if (sessionStorage.getItem(STORAGE_AUTH_DATA)) {
      const auth = JSON.parse(sessionStorage.getItem(STORAGE_AUTH_DATA));
      setAppName(auth[STORAGE_AUTH_TENANT_APP_NAME]);
    }
  }, []);

  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <NavBar />
        <Box className="success-page-body">
          <label className="success-title-msg">
            Thank you for connecting you accounts!
          </label>
          <label className="success-description-msg">
            That was easy! You can now choose to{" "}
            <a href="#" onClick={() => navigate(ROUTE_LANDING_PAGE)}>
              connect more accounts
            </a>{" "}
            if you'd like. If you have questions or concerns, please contact{" "}
            {appName}.
          </label>
        </Box>
      </Background>
    </>
  );
}

export default SuccessPage;
