import { CircularProgress } from "@mui/material";
import { Box } from "@mui/system";
import React, { useState, useRef } from "react";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  MICROSITE_CONNECT_ACCOUNT_FAILED,
  MICROSITE_CONNECT_ANOTHER_ACCOUNT,
  MICROSITE_CONNECT_MORE_ACCOUNTS,
  MICROSITE_CONNECT_TRY_AGAIN,
  trackEvent,
} from "../../analytics";
import { Background, FullScreenLoader, InvertedPrimaryButton, NavBar, PrimaryButton } from "../../Components";
import { ROUTE_LANDING_PAGE } from "../../Routes/RouteMapping";
import { getWorkplatformDetails } from "../../utils/api";
import { CONNECTED_STORAGE_WORK_PLATFORM_ID, STORAGE_AUTH_DATA, STORAGE_WORK_PLATFORM_ID } from "../../utils/constants";
import { launchSDK, navigateToPlatformEducationPage } from "../../utils/helpers";
import "./AbandonedPage.scss";

function AbandonedPage() {
  const [loader, setLoader] = useState(false);
  const [workplatformDetails, setWorkplatformDetails] = useState();
  const navigate = useNavigate();
  const onlyOnce = useRef(true);

  useEffect(() => {
    (async () => {
      const auth = JSON.parse(sessionStorage.getItem(STORAGE_AUTH_DATA));
      if (onlyOnce.current) {
        onlyOnce.current = false;
        const workPlatformId = sessionStorage.getItem(CONNECTED_STORAGE_WORK_PLATFORM_ID);
        const response = await getWorkplatformDetails(workPlatformId);
        trackEvent(MICROSITE_CONNECT_ACCOUNT_FAILED, { creator_user_id: auth?.user_id, work_platform_name: response?.name });
        setWorkplatformDetails(response);
      }
    })();
  }, []);

  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <FullScreenLoader showLoader={loader} />
        <Box className="abandoned-page-body">
          <label className="abandoned-title-msg">You did not connect your account :(</label>
          <label className="abandoned-desc-msg">
            We'd love to help you try again or connect a different account. We understand that some requested permissions might seem scary, don't
            worry - we won't post, modify, or delete anything.
          </label>
          <Box className="abandoned-btns-container">
            <PrimaryButton
              label={"Try again"}
              onClick={() => {
                trackEvent(MICROSITE_CONNECT_TRY_AGAIN, { work_platform_name: workplatformDetails?.name });
                navigateToPlatformEducationPage(navigate);
              }}
            />
            <InvertedPrimaryButton
              label={"Connect other account"}
              onClick={() => {
                trackEvent(MICROSITE_CONNECT_ANOTHER_ACCOUNT, { work_platform_name: workplatformDetails?.name });
                navigate(ROUTE_LANDING_PAGE);
              }}
            />
          </Box>
        </Box>
      </Background>
    </>
  );
}

export default AbandonedPage;
