const url = "http://ec2-34-206-221-117.compute-1.amazonaws.com/v1/ai/caption/generate"; 

export const generatePost = async ( urlSearchInput, description, recommendationNumber, wordsLimit, language, platform ) => {
debugger;
  try {
    const response = await fetch(url, {
      method: "POST",
      body: JSON.stringify({
        "image_url": urlSearchInput,
        "text_caption": description,
        "recommendation_count": recommendationNumber,
        "words_limit": wordsLimit,
        "platform": platform,
        "lang": language
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });

    // Handle the response data if needed
    const data = await response.json();
    console.log(data); // Log or process the response data
  } catch (error) {
    console.error("Error:", error.message);
  }
};

const upload = async ({ fileList: newFileList }) => {
  setFileList(newFileList);

  const formData = new FormData();
  formData.append('image', newFileList[0].originFileObj);

  try {
    const response = await fetch('https://api.example.com/upload', {
      method: 'POST',
      body: formData,
    });

    if (response.ok) {
      // Image uploaded successfully
      const result = await response.json();
      console.log(result);
    } else {
      // Handle the error case
      console.error('Image upload failed');
    }
  } catch (error) {
    // Handle any network or fetch-related errors
    console.error('Error occurred during image upload', error);
  }
};
