import React from "react";
import { InvertedPrimaryButton, PrimaryButton } from "../index";
import "./GenericMessage.scss"
function InstagramGenericMessage() {
  return (
    <div className="message-container">
      <h2 className="message-title">Before you proceed, please remember</h2>
      <div>
        <p className="message-point" >Instagram has some mandatory login requirements. This allows us to get your profile, audience, and campaign data.</p>
        <ol value="1">
          <li className="message-point">Your Instagram account needs to be a Creator or Business account.
            <p className="sub-point">See how to convert your account to a Creator account.</p>
            <video className="video-container" style={{ border: '1px solid #E3E3E3', marginBottom: '1.5rem' }} width="5" height="100%" controls controlsList="nodownload">
              <source src="https://cdn.getphyllo.com/guides/videos/instagram/convert_to_creator_type.m4v" type="video/mp4" />
              Video
            </video>
            <a href="https://help.getphyllo.com/?lang=en">Need more help? Check the help guide here.</a>
          </li>
          <li className="message-point">Your Instagram account needs to be linked to a Facebook Page.
            <p className="sub-point">See how to link your Instagram account to a Facebook page.</p>
            <video className="video-container" style={{ border: '1px solid #E3E3E3', marginBottom: '1.5rem' }} width="100%" height="100%" controls controlsList="nodownload">
              <source src="https://cdn.getphyllo.com/guides/videos/instagram/link_fb_page.m4v" type="video/mp4" />
              Video
            </video>
            <a href="https://help.getphyllo.com/?lang=en">Need more help? Check the help guide here.</a>
          </li>
          <li className="message-point">See how to link your Instagram account to a Facebook page.</li>
          <li className="message-point">While logging in, please select the correct combination of Instagram account and Facebook page.</li>
        </ol>
      </div>
      <div className="button-group" >
        <PrimaryButton
          // onClick={}
          htmlType="submit"
          label="Proceed"
          style={{ marginRight: "24px" }}
        // loading={loading}
        />
        <InvertedPrimaryButton
          // onClick={}
          htmlType="submit"
          label="Back"
        // loading={loading}
        />
      </div>
    </div>
  );
}

export default InstagramGenericMessage;
