import React from "react";
import { Background, NavBar, PrimaryButton } from "../index";
import "./GenericMessage.scss"
function GenericMessage() {
  return (
    <div className="message-container">
      <div>
        <h2 className="message-title">Before you proceed, please remember</h2>
        <p className="message-point" >1. You need to log into your social account using the correct credentials. </p>
        <p className="message-point" >2. When you log in, we'll need your permission to access your account data (profile, audience, and content). Don't worry - we won't post, modify, or delete anything. </p>
        <p className="message-point" >3. The account is connected securely through Phyllo. </p>
        <p className="message-point" >4. You can disconnect your account whenever you choose to.</p>
      </div>

        <PrimaryButton
                  disabled={disableSubmit() || (formInstance.getFieldValue("is_active") && !formInstance.getFieldValue("privacy_policy_url"))}
                  onClick={redirectToCalendlyPage}
                  htmlType="submit"
                  label="Book a meeting"
                  style={{ marginRight: "24px" }}
                  loading={loading}
                />
    </div>
  );
}

export default GenericMessage;
