import { CircularProgress } from "@mui/material";
import { Box } from "@mui/system";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Background,
  FullScreenLoader,
  InvertedPrimaryButton,
  NavBar,
  PrimaryButton,
} from "../../Components";
import { launchSDK } from "../../utils/helpers";
import "./AbandonedPage.scss";

function AbandonedPage() {
  const [loader, setLoader] = useState(false);
  const navigate = useNavigate();

  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <FullScreenLoader showLoader={loader} />
        <NavBar />
        <Box className="abandoned-page-body">
          <label className="abandoned-title-msg">
            You did not connect your account :(
          </label>
          <label className="abandoned-desc-msg">
            We'd love to help you try again or connect a different account. We
            understand that some requested permissions might seem scary, don't
            worry - we won't post, modify, or delete anything.
          </label>
          <Box className="abandoned-btns-container">
            <PrimaryButton
              label={"Try again"}
              onClick={() => launchSDK(setLoader, navigate)}
            />
            <InvertedPrimaryButton
              label={"Connect other account"}
              onClick={() => launchSDK(setLoader, navigate, null)}
            />
          </Box>
        </Box>
      </Background>
    </>
  );
}

export default AbandonedPage;
