import { CircularProgress } from "@mui/material";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { ROUTE_LANDING_PAGE } from "../../Routes/RouteMapping";
import { launchSDK } from "../../utils/helpers";
import {
  InvertedPrimaryButton,
  PrimaryButton,
  FullScreenLoader,
} from "../index";
import "./GenericMessage.scss";
function GenericMessage() {
  const [loader, SetLoader] = useState(false);
  const navigate = useNavigate();

  return (
    <div className="message-container">
      <FullScreenLoader showLoader={loader} />
      <div>
      <h2 className="message-title">Before you proceed, please remember</h2>
      <ol style={{marginLeft:"-11px"}} value="1">
        <li className="message-point" >You need to log into your social account using the correct credentials. </li>
        <li className="message-point" >Onc you log in, we'll need your permission to access your account data (profile, audience, and content). Don't worry - we won't post, modify, or delete anything. </li>
        <li className="message-point" >The account is connected securely through Phyllo. </li>
        <li className="message-point" >You can disconnect your account whenever you choose to.</li>
      </ol>
      </div>
      <div className="button-group">
        <PrimaryButton
          onClick={() => launchSDK(SetLoader, navigate)}
          htmlType="submit"
          label="Proceed"
          style={{ marginRight: "24px" }}
          // loading={loading}
        />
        <InvertedPrimaryButton
          onClick={() => navigate(ROUTE_LANDING_PAGE)}
          htmlType="submit"
          label="Back"
          // loading={loading}
        />
      </div>
    </div>
  );
}

export default GenericMessage;
