import { useEffect, useRef, useState } from "react";
import { Box } from "@mui/system";
import React from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import Icons from "../../Components/Icons/icons";
import { Background, ButtonWithGlyphIcon, FullScreenLoader, NavBar, PrimaryButton } from "../../Components/index";
import InputField from "../../Components/InputField/InputField";
import "./LandingPage.scss"
import TagsFilter from "../../Components/TagsFilter/TagsFilter";
import { sendData } from "../../utils/api";
import { TryCatch } from "@sentry/react";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import { languages } from "../../utils/constants";
import { Input } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import { Modal, Upload } from 'antd';
const { TextArea } = Input;
const getBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });
function LandingPage() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [urlSearchInput, setUrlSearchInput] = useState("");
  const [description, setDescription] = useState("");
  const [recommendationNumber, setRecommendationNumber] = useState(3);
  const [wordsLimit, setWordsLimit] = useState(400);
  const [language, setLanguage] = useState("en");
  const [previewOpen, setPreviewOpen] = useState(false);
  const [previewImage, setPreviewImage] = useState('');
  const [previewTitle, setPreviewTitle] = useState('');
  const [fileList, setFileList] = useState([
  ]);
  const handleCancel = () => setPreviewOpen(false);
  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewOpen(true);
    setPreviewTitle(file.name || file.url.substring(file.url.lastIndexOf('/') + 1));
  };
  const handleChange = ({ fileList: newFileList }) => setFileList(newFileList);
  const uploadButton = (
    <div>
      <PlusOutlined />
      <div
        style={{
          marginTop: 8,
        }}
      >
        Upload
      </div>
    </div>
  );
  const asyncRequest = async ({ file, onSuccess }) => {
    setTimeout(() => {
      onSuccess("ok");
    }, 0);
  }

  console.log(fileList)


  const handleShowResults = async () => {
    try {
      const response = await sendData(urlSearchInput, description, recommendationNumber, wordsLimit, language)
      console.log(response)

    } catch (error) {

    }
  }

  if (isLoading) {
    return <FullScreenLoader showLoader={isLoading} />;
  }

  return (
    <>
      <Background>
        <Box className="landing-page-body">
          <InputField
            inputLabel="Enter Image Url"
            placeholder="start typing"
            value={urlSearchInput}
            onChange={(e) => {
              setUrlSearchInput(e.target.value);
            }}
            autoFocus
          />
          <Box sx={{ marginTop: "30px" }}>
            <label style={{ marginBottom: "5px" }} htmlFor="input-field" className="input-label" >
              Enter Description
            </label>
            <TextArea style={{ marginTop: "10px" }} rows={4} placeholder="start typing" />
          </Box>
          <Box sx={marginTop}>
            <Upload
              action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
              listType="picture-card"
              fileList={fileList}
              customRequest={asyncRequest}
              onPreview={handlePreview}
              onChange={handleChange}
            >
              {fileList.length >= 1 ? null : uploadButton}
            </Upload>
            <Modal open={previewOpen} title={previewTitle} footer={null} onCancel={handleCancel}>
              <img
                alt="example"
                style={{
                  width: '100%',
                }}
                src={previewImage}
              />
            </Modal>
          </Box>
          <Box>
            <FormControl sx={{ minWidth: "45%", mt: 5, mr: "10%" }}>
              <InputLabel id="demo-simple-select-helper-label">Recommendation number</InputLabel>
              <Select
                labelId="demo-simple-select-helper-label"
                id="demo-simple-select-helper"
                value={recommendationNumber}
                label="Recommendation Number"
                onChange={(event) => setRecommendationNumber(event.target.value)}
              >
                {[1, 2, 3, 4, 5].map((value) => (
                  <MenuItem value={value}>{value}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl sx={{ minWidth: "45%", mt: 5 }}>
              <InputLabel id="demo-simple-select-helper-label">Words limit</InputLabel>
              <Select
                labelId="demo-simple-select-helper-label"
                id="demo-simple-select-helper"
                value={wordsLimit}
                label="Words limit"
                onChange={(event) => setWordsLimit(event.target.value)}
              >
                {[100, 200, 300, 400, 500].map((value) => (
                  <MenuItem value={value}>{value}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
          <Box>
            <FormControl sx={{ minWidth: "45%", mt: 5 }}>
              <InputLabel id="demo-simple-select-helper-label">Select language</InputLabel>
              <Select
                labelId="demo-simple-select-helper-label"
                id="demo-simple-select-helper"
                value={language}
                label="Select language"
                onChange={(event) => setLanguage(event.target.value)}
              >
                {languages.map((value) => (
                  <MenuItem value={value.code}>{value.name}</MenuItem>
                ))}
              </Select>
            </FormControl>

          </Box>
          <Box className="showResultButton">
            <PrimaryButton label="Generate Post" onClick={handleShowResults} />
          </Box>
        </Box>
      </Background>
    </>
  );
}

export default LandingPage;
