import { Box } from "@mui/system";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Background,
  FullScreenLoader,
  InvertedPrimaryButton,
  NavBar,
  PrimaryButton,
} from "../../Components";
import { ROUTE_LANDING_PAGE } from "../../Routes/RouteMapping";
import { getWorkplatformDetails } from "../../utils/api";
import {
  STORAGE_WORK_PLATFORM_ID,
  CONNECTED_STORAGE_WORK_PLATFORM_ID,
} from "../../utils/constants";
import {
  launchSDK,
  navigateToPlatformEducationPage,
} from "../../utils/helpers";
import "./ErrorPage.scss";
function ErrorPage() {
  const [loader, setLoader] = useState(false);
  const navigate = useNavigate();
  const [workplatformDetails, setWorkplatformDetails] = useState();
  useEffect(() => {
    (async () => {
      const workPlatformId = sessionStorage.getItem(STORAGE_WORK_PLATFORM_ID);
      if (workPlatformId) {
        const response = await getWorkplatformDetails(workPlatformId);
        if (response) {
          setWorkplatformDetails(response);
        }
      }
    })();
  }, []);
  return (
    <>
      <Background rightLeaves leftLeaves rightScratched>
        <FullScreenLoader showLoader={loader} />
        <NavBar />
        <Box className="error-page-body">
          <label className="error-title-msg">
            Having issues while connecting the account?
          </label>
          <label className="error-desc-msg">
            The connection could have failed due to the possible reasons:
            <ol>
              {workplatformDetails &&
                workplatformDetails.error_messages.error_message_info.map(
                  (item) => {
                    return <li>{item}</li>;
                  }
                )}
            </ol>
          </label>
          <label className="error-support-msg">
            Weâ€™d love to help you try again or connect a different account. If
            you have queries, please get in touch at{" "}
            <a href="mailto:support@getphyllo.com">support@getphyllo.com</a>.
          </label>
          <Box className="error-btns-container">
            <PrimaryButton
              label={"Try again"}
              onClick={() => navigateToPlatformEducationPage(navigate)}
            />
            <InvertedPrimaryButton
              label={"Connect other account"}
              onClick={() => navigate(ROUTE_LANDING_PAGE)}
            />
          </Box>
        </Box>
      </Background>
    </>
  );
}

export default ErrorPage;
